const Base = require('../lib/mongo/base')

class Notification extends Base {

    constructor(config) {
        super(config, 'notificaiton');
    }

    getAllNotifications(userId, next) {
        var payload = {
           userId,
           sort: { timestamp: -1 }
        }
        this.find(payload, next)
    }

    deleteAllNotifications(userId, next) {
        var payload = {
           userId
        }
        this.remove(payload, (err) => {
            next(err)
        })
    }

}

module.exports = Notification