const Product = require('./product');

class ProductOld extends Product {

    constructor(config, country) {
        super(config, country, true);
        this.country = country;
    }

    scrollFirstAmazonCategory(payload, next) {
        let body = {
            size: payload.size,
            scroll: payload.scroll,
            type: payload.type,
            query: {
                query: {
                    bool: {
                        must: [{
                            term: {
                                category: payload.category
                            }
                        }]
                    }
                }
            }
        };
        this.scrollPage(body, next);
    }

}

module.exports = ProductOld;
