const should = require('should');
const cleanUpStalledJob = require('../lib/cleanUpStalledJob')

const config = require('../config').es;

describe('Clean Up Stalled Job', function() {

        it('should return success if no queued jobs are found', function(done) {

            cleanUpStalledJob({
                jobId: "78a75c8f03605e3334d2f909",
                userId: "58a75c8f03605e3334d2f909"
            }, (err, result) => {
                should(err).be.equal('NO_QUEUED');
                should(result.job).be.equal(undefined);
                done();
            })

        })

})
