const should = require('should');
const DistributeModel = require('../index.js').model.distribution

const config = { "host" : "localhost:9200" }

describe('distribution test', function() {
        this.timeout(10000)

        it('should find product by id', function(done) {
            var Distribute = new DistributeModel(config, {
                country: 'us',
                userName: 'mike',
                wishlist: true
            })

            Distribute.addOppToPlan({
                id: "3a6b719081a148e8a4d7d078c3973bd6",
                domain:"www-ltdcommodities-com"
            }, (err, result) => {
                should(err).be.equal(null)
                done()
            })


        })
})