const expect = require('chai').expect;
const SupplierModel = require('../model/supplier');

const config = {
    mongoDb: {
        db: "sm",
        host: "localhost",
        port: 27017
    }
};


describe('get a list of suppliers by domain', () => {

    let supplier;

    beforeEach(()=>{
        supplier = new SupplierModel(config.mongoDb, {
            country: 'uk'
        });
    });

    it('should provide a list', (done) => {
        supplier.generateDomainLookup((err, data)=>{
            expect(err).to.equal(null);
            console.log(JSON.stringify(data, null, 2));
            done();
        });
    });

});
