const should = require('should');
const MigateModel = require('../index.js').model.migrate

const config = require('../config').es;

describe('Migrate Test', function () {

    it('should return an array of ASINS', function (done) {

        var Migrate = new MigateModel(config, {
            country: 'uk'
        })

        Migrate.getMatchedAsins('www-toysrus-co-uk', (err, result) => {
            should(err).be.equal(null);            
            done();
        })


    })

    it('should return the Amazon Records for array of ASINS', function (done) {

        var Migrate = new MigateModel(config, {
            country: 'uk'
        })

        Migrate.getRecordsForAsins(['B0155A9YRO',
            'B00HYWOS82',
            'B01IGKGW0C',
            'B00SYQ9ZGW',
            'B01NBTCSY8',
            'B0155AA0EK',
            'B017M3ADG8'
        ], (err, result) => {
            should(err).be.equal(null);            
            done();
        })

    })


})