const should = require('should')
const OpportuntiyModel = require('../index.js').model.opportunity
const config = require('../config').es;


describe('Opportunity fixWishListForBrandRestrictions Test', function () {

    let payload;
    this.timeout(10000000)

    it('should update thw wishlist records if they have no brand restrictions data', (done)=>{

        var Opportunity = new OpportuntiyModel(config, {
            country: 'us',
            userName: 'martha',
            wishlist: true
        });

        payload = {
            size: 500
        }

        Opportunity.fixWishListForBrandRestrictions(payload, (err, result) => {
            should(err).be.equal(null)
            done();
        });
    });


})
