const should = require('should');
const SearchModel = require('../index.js').model.search
const config = {
                    db: "se",
                    host: "127.0.0.1",
                    port: 27017
                }

describe('Search Test', function() {

        it('should return Searches as array', function(done) {
                var Search = new SearchModel(config)

                Search.find({
                    size: 10
                }, (err, result) => {
                    should(err).be.equal(null)
                    result.should.have.property('total')
                    result.should.have.property('records')
                    result.total.should.be.above(0)
                    result.records.length.should.be.equal(10)
                    console.log(result)
                    done();
                })

        })

        it('should return One Search', function(done) {
            var Search = new SearchModel(config)
            Search.findById("592fe90299d27365821b79ce", (err, result) => {
                should(err).be.equal(null)
                done()
            })
        })

})
